<br> <label>Comment</label><br> <textarea name="comment"></textarea><br> <button type="submit">Save record</button> <button type="reset">Reset</button> </form>', function(opts) {
        var self = this

        self.brood = ['1.0','2.0','2+','3+']
        self.gender = ['male','female']
        self.smallNum = []
        self.largeNum = []

        for (var i = 0; i < 10; i++) {
            self.smallNum[i] = i;
        };

        for (var i = 0; i < 100; i++) {
            self.largeNum[i] = i;
        };

        this.getSpecies = function() {
            if (self.species.value) {
               riotcontrol.trigger('newring_species', self.species.value)
            } else {
                self.update({specieslist: []})
            }
        }.bind(this);

        this.setSpecies = function(e) {
            console.log(e.item);
            self.species.value = e.item.Code
            self.size.value = e.item.Type1.replace(',','.')
            console.log(self.size.selectedIndex);
            self.setSnid()
            self.update({specieslist: []})
            self.species.blur()
        }.bind(this);

        this.setSnid = function() {
            var item = self.size.options[self.size.selectedIndex]
            self.snid.value = item.getAttribute('data-snid')
        }.bind(this);

        this.save = function() {
            var data = {
                species: self.species.value,
                ring: {
                    id: self.ring.value,
                    snid: self.snid.value
                },
                net: self.net.value,
                age: self.age.value,
                sex: self.sex.value,
                pjm: self.pjm.value,
                fat: self.fat.value,
                sign: self.sign.value,
                weight: self.weight.value,
                wing: {
                    length: self.wingl.value,
                    primaries: self.hand.value,
                    secondaries: self.arm.value
                },
                comment: self.comment.value
            }
            console.log('data',data);
        }.bind(this);

        riotcontrol.on('newring_ringsize', function(data) {
            self.update({ringsize: data})
        })

        riotcontrol.on('newring_species_data', function(data) {
            self.update({specieslist: data})
        })

});
