var riot = require('riot');
var agent = require('superagent');
var moment = require('moment');

module.exports = function() {
	riot.observable(this);

	var self = this, map, marker;
	var geocoder = new google.maps.Geocoder();
	var annsjon = new google.maps.LatLng(63.27114510000001, 12.449269500000014);
	var mapOptions = {
		zoom: 8,
		center: annsjon,
		mapTypeId: google.maps.MapTypeId.TERRAIN,
		streetViewControl: false,
		zoomControl: false,
		scaleControl: false
	};

	function init() {
		map = new google.maps.Map(document.getElementById('mapCanvas'), mapOptions);

		google.maps.event.addListener(map, 'click', function(e){

			var location = e.latLng.lat()+','+e.latLng.lng();
			var timestamp = moment().unix();

			// Google Maps Local Time
			var url = 'https://maps.googleapis.com/maps/api/timezone/json?location='+location+'&timestamp='+timestamp;
			agent.get(url).end(function(err, res) {
				console.log(new Date((timestamp + res.body.dstOffset) * 1000));
				// console.log(moment().utc(timestamp).format());
			});

			// Marker
			addMarker(e.latLng);
			setPositionData(e.latLng);
		});
	}

	function addMarker(location) {
		if (marker) {
			clearMarker();
		}
		marker = new google.maps.Marker({
			position: location,
			map: map
		});
	}

	function clearMarker() {
		marker.setMap(null);
	}

	function setPositionData(location) {
		var country, data = {};
		geocoder.geocode({'latLng': location}, function(results, status) {
			if (status == google.maps.GeocoderStatus.OK) {
				if (results[0]) {
					country = results[results.length - 1].address_components[0];
					data.latLng = location.lat() +', '+ location.lng();
					data.countryIso = country.short_name;
					data.countryName = country.long_name;
					self.trigger('map_data', data);
				}
			}
		});
	}

	self.on('map_init', init);
	self.on('map_reset', clearMarker);
};
var riot = require('riot');
var utils = require('../../utils');
var fbRef = require('../../firebase');

module.exports = function() {
    riot.observable(this);

    var self = this, uid, pattern, category, result;
    var types = ['title','site','ownerName'];
    var publicRef = fbRef.child('project').orderByChild('public').equalTo(true);

    function search(data) {
        uid = fbRef.getAuth().uid;
        pattern = new RegExp(data.needle, 'i');
        category = data.category;
        result = [];

        publicRef.once('value', function(publicSnap) {
            publicSnap.forEach(function(projectSnap) {
                if (category == 'all') {
                    tryCategories(projectSnap);
                } else {
                    tryCategory(projectSnap);
                }
            });

            // Return a complete data set, in reversed order (newst first).
            self.trigger('search_data', result.reverse());
        });
    }

    function tryCategories(snap) {
        var project = snap.val(), sample;
        for (var i = 0; i < types.length; i++) {
            sample = project[types[i]];
            if (sample && pattern.test(sample)) {
                addResult(project, snap.key());
                break;
            }
        }
    }

    function tryCategory(snap) {
        var project = snap.val();
        var sample = project[category];
        if (sample && pattern.test(sample)) {
            addResult(project, snap.key());
        }
    }

    function addResult(project, id) {
        var extras = {
            pid: id,
            isOwner: uid === project.ownerId,
            ownerName: uid === project.ownerId ? 'You' : project.ownerName,
            isActive: new Date(project.dateEnd).getTime() > new Date().getTime()
        };
        result.push(utils.extend(project, extras));
    }

    function clearOnRoute(route) {
        if (route == 'search') {
            self.trigger('search_clear');
        }
    }

    self.on('search', search);
    self.on('route', clearOnRoute);
};
