var riot = require('riot');
var utils = require('../../utils');
var firebase = require('firebase');

module.exports = function() {
	riot.observable(this);

	var self = this;

	var fbRef = new firebase('https://bluebird.firebaseio.com/');

	var createProject = function(data) {
		var uid = fbRef.getAuth().uid;

		// Firebase locations.
		var projectRef = fbRef.child('project');
		var countryRef = fbRef.child('country/' + data['countryIso']);
		var userRef = fbRef.child('user/' + uid);

		// Save project.
		var newProjectRef = projectRef.push({
			"public": data['status'] === 'public',
			"ownerId": uid,
			"ownerName": utils.getLocalUser('name'),
			"title": data['title'],
			"site": data['site'],
			"latlng": data['latlng'],
			"country": data['countryIso'],
			"dateStart": data['dateStart'],
			"dateEnd": data['dateEnd']
		});

		// Get ID of saved project.
		var projectID = newProjectRef.key();

		// Add projectID to user profile.
		userRef.child('project/own/' + projectID).set(true);

		// Store Country data (if countryIso does not already exist).
		countryRef.once('value', function(snap) {
			if (snap.val() === null) {
				countryRef.set({full: data.countryName});
			}
			// List project under Country.
			countryRef.child('project/' + projectID).set(true);
		});

		var route = 'profile';
		// var route = 'project/'+ projectID;
		riot.route(route);
	}

	self.on('project_create', createProject);
}
var riot = require('riot');
var utils = require('../../utils');
var firebase = require('firebase');

module.exports = function() {
	riot.observable(this);

	var self = this, uid;

	var fbRef = new firebase('https://bluebird.firebaseio.com/');

	// Get the user object (triggered on route).
	function userUpdate() {
		uid = fbRef.getAuth().uid;

		var localUser = utils.getLocalUser();

		// Run once and again if user name changes.
		fbRef.child('user/' + uid + '/name').on('value', function(snap) {
			var name = snap.val();
			if (name !== null && name !== localUser['name']) {
				utils.updateLocalUser('name', name);
				self.trigger('user_updated', utils.getLocalUser());
			}
		});

		self.trigger('user_updated', localUser);
	}

	// Alerts on success or failure.
	function updateComplete(error) {
		if(error) {
			self.trigger('alert', {text: error.message, type:'danger'});
		}else{
			self.trigger('alert', {text: 'Profile successfully updated!', type:'success'});
		}
	}

	// Triggered on route.
	self.on('user_update', userUpdate);

	// Triggerd by actions.
	self.on('user_update_name', function(params) {
		fbRef.child('user/' + uid).update(params, updateComplete);
	});

	self.on('user_update_email', function(params) {
		fbRef.changeEmail(params, updateComplete);
	});

	self.on('user_update_password', function(params) {
		fbRef.changePassword(params, updateComplete);
	});
}
var riot = require('riot');

module.exports = function() {
	riot.observable(this);

	var self = this, marker, geocoder;

	function map_init() {
		var delta = new google.maps.LatLng(63.27114510000001, 12.449269500000014);
		var mapOptions = {
			zoom: 8,
			center: delta,
			mapTypeId: google.maps.MapTypeId.TERRAIN,
			streetViewControl: false,
			zoomControl: false,
