module.exports = function() {
	riot.observable(this);

	var self = this;

	var createProject = function(data) {
		var uid = fbRef.getAuth().uid;

		// Firebase locations.
		var projectRef = fbRef.child('project');
		var countryRef = fbRef.child('country/' + data['countryIso']);
		var userRef = fbRef.child('user_project/' + uid + '/own/');

		// Save project.
		var newProjectRef = projectRef.push({
			'title': data['title'],
			'site': data['site'],
			'ownerId': uid,
			'ownerName': utils.getLocalUser('name'),
			'country': data['countryIso'],
			'latlng': data['latlng'],
			'dateStart': data['dateStart'],
			'dateEnd': data['dateEnd'],
			'public': data['status'] === 'public'
		});

		// Get ID of saved project.
		var projectId = newProjectRef.key();

		// Add projectId to user profile.
		userRef.child(projectId).set(true);

		countryRef.once('value', function(snap) {
			// Store Country (if countryIso doesn't exist).
			if (!snap.exists()) {
				countryRef.set({full: data.countryName});
			}
			// List project under Country.
			countryRef.child('project/' + projectId).set(true);
		});

		var route = 'profile'; // 'project/' + projectId
		riot.route(route);
	}

	self.on('project_create', createProject);
}
var riot = require('riot');

module.exports = function() {
	riot.observable(this);

	var self = this, map, marker;
	var geocoder = new google.maps.Geocoder();
	var annsjon = new google.maps.LatLng(63.27114510000001, 12.449269500000014);
	var mapOptions = {
		zoom: 8,
		center: annsjon,
		mapTypeId: google.maps.MapTypeId.TERRAIN,
		streetViewControl: false,
		zoomControl: false,
		scaleControl: false
	};

	function init() {
		map = new google.maps.Map(document.getElementById('mapCanvas'), mapOptions);

		google.maps.event.addListener(map, 'click', function(e){
			addMarker(e.latLng);
			setPositionData(e.latLng);
		});
	}

	function addMarker(location) {
		if(marker) {
			clearMarker();
		}
		marker = new google.maps.Marker({
			position: location,
			map: map
		});
	}

	function clearMarker() {
		marker.setMap(null);
	}

	function setPositionData(location) {
		var country, data = {};
		geocoder.geocode({'latLng': location}, function(results, status) {
			if(status == google.maps.GeocoderStatus.OK) {
				if(results[0]) {
					country = results[results.length - 1].address_components[0];
					data.latLng = location.lat() +', '+ location.lng();
					data.countryIso = country.short_name;
					data.countryName = country.long_name;
					self.trigger('map_data', data);
				}
			}
		});
	}

	self.on('map_init', init);
	self.on('map_reset', clearMarker);
};
var riot = require('riot');
